namespace Lof\FlashSales\Model\Adminhtml\System\Config\Source;

class DisplayAddToCartMode implements \Magento\Framework\Option\ArrayInterface
{

    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [
            ['value' => 1, 'label' => __('Hide button Add to cart')],
            ['value' => 0, 'label' => __('Custom Add to Cart Button')]
        ];
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [
            1 => __('Hide button Add to cart'),
            0 => __('Custom Add to Cart Button')
        ];
    }
}
