namespace Lof\FlashSales\Model\Adminhtml\System\Config\Source;

class EventColumn implements \Magento\Framework\Option\ArrayInterface
{

    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [
            ['value' => 1, 'label' => __('1 Event Column')],
            ['value' => 2, 'label' => __('2 Event Column')],
            ['value' => 3, 'label' => __('3 Event Column')],
        ];
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [
            1 => __('1 Event Column'),
            2 => __('2 Event Column'),
            3 => __('3 Event Column'),
        ];
    }
}
